mapper.select:
SELECT B.BOARD_NO, B.BOARD_TITLE, B.BOARD_CONTENT, TO_CHAR(B.BOARD_WRITE_DATE, 'YYYY-MM-DD') AS BOARD_WRITE_DATE, B.READ_COUNT, M.MEMBER_NICKNAME, B.MEMBER_NO, B.BOARD_LOCK, B.BOARD_PW, B.BOARD_CODE, (SELECT I.IMG_PATH || I.IMG_RENAME FROM BOARD_IMG I WHERE I.BOARD_NO = B.BOARD_NO AND I.IMG_ORDER = 0) AS THUMBNAIL_PATH,
VOLUNTEER_START, VOLUNTEER_END, VOLUNTEER_CATEGORY, VOLUNTEER_AGENCY, VOLUNTEER_TYPE,
(SELECT COUNT(*) FROM BOARD_LIKE L WHERE L.BOARD_NO = B.BOARD_NO) AS LIKE_COUNT, (SELECT COUNT(*) FROM BOARD_LIKE L WHERE L.BOARD_NO = B.BOARD_NO AND L.MEMBER_NO = #{memberNo}) AS LIKE_CHECK, (SELECT COUNT(*) FROM SCRAP S WHERE S.BOARD_NO = B.BOARD_NO AND S.MEMBER_NO = #{memberNo}) AS SCRAP_CHECK FROM BOARD B JOIN MEMBER M ON B.MEMBER_NO = M.MEMBER_NO WHERE B.BOARD_NO = #{boardNo} AND B.BOARD_CODE = #{boardCode}
