mapper.select:
SELECT BOARD_NO, BOARD_TITLE FROM "BOARD" WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO < #{boardNo}
AND BOARD_DEL_FL = 'N' ORDER BY BOARD_NO DESC FETCH FIRST 1 ROW ONLY

SELECT BOARD_NO, BOARD_TITLE FROM "BOARD" WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO > #{boardNo}
AND BOARD_DEL_FL = 'N' ORDER BY BOARD_NO ASC FETCH FIRST 1 ROW ONLY

mapper.delete:
DELETE FROM "BOARD_LIKE"
WHERE MEMBER_NO = #{memberNo}
AND BOARD_NO = #{boardNo}

DELETE FROM SCRAP
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}
