mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT * FROM INFO_BOARD
AND PROGRM_NM LIKE '%' || #{progrmNm} || '%'
AND NANMM_BY_NM LIKE '%' || #{nanmmByNm} || '%'
AND SCH_SIDO = #{schSido}
AND SCH_SIGN = #{schSign}
AND (ACT_RM NOT LIKE '%보건%' AND ACT_RM NOT LIKE '%생활%' AND ACT_RM NOT LIKE '%교육%' AND ACT_RM NOT LIKE '%환경%' AND ACT_RM NOT LIKE '%행정%' AND ACT_RM NOT LIKE '%문화%' AND ACT_RM NOT LIKE '%안전%' AND ACT_RM NOT LIKE '%인권%' AND ACT_RM NOT LIKE '%재난%')
AND ACT_RM LIKE '%' || #{actRm} || '%'
AND (ACT_END_TM - ACT_BEGIN_TM) >= 8 AND (ACT_END_TM - ACT_BEGIN_TM) = #{searchTime}
AND ADULT_POSBL_AT = 'Y' AND YNG_BGS_POSBL_AT = 'Y'
ORDER BY INFO_BOARD_NO DESC ) A ) WHERE RNUM BETWEEN #{offset} + 1 AND #{offset} + #{limit}
