mapper.update:
UPDATE "MEMBER"
SET 
MEMBER_TEL      = #{memberTel},
MEMBER_ADDRESS  = #{memberAddress}


	, PROFILE_IMG = #{profileImg}


WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*) FROM ( SELECT B.BOARD_NO FROM "SCRAP" S JOIN "BOARD" B ON (S.BOARD_NO = B.BOARD_NO) WHERE S.MEMBER_NO = #{memberNo} AND B.BOARD_DEL_FL = 'N'
AND (B.BOARD_TITLE LIKE '%' || #{query} || '%' OR B.BOARD_CONTENT LIKE '%' || #{query} || '%')
UNION ALL
SELECT I.INFO_BOARD_NO FROM "INFO_SCRAP" S JOIN "INFO_BOARD" I ON (S.INFO_BOARD_NO = I.INFO_BOARD_NO) WHERE S.MEMBER_NO = #{memberNo}
AND (I.PROGRM_NM LIKE '%' || #{query} || '%' OR I.PROGRM_CN LIKE '%' || #{query} || '%')
)
