mapper.update:
UPDATE "MEMBER"
SET 
	MEMBER_NICKNAME = #{memberNickname},
	MEMBER_TEL      = #{memberTel},
	MEMBER_ADDRESS  = #{memberAddress}


		, MEMBER_PW = #{memberPw}



		, PROFILE_IMG = #{profileImg}


WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT SUM(CNT) FROM ( SELECT COUNT(*) CNT FROM "SCRAP" S JOIN "BOARD" B ON (S.BOARD_NO = B.BOARD_NO) WHERE S.MEMBER_NO = #{memberNo} AND B.BOARD_DEL_FL = 'N'
AND (B.BOARD_TITLE LIKE '%' || #{query} || '%' OR B.BOARD_CONTENT LIKE '%' || #{query} || '%')
UNION ALL
SELECT COUNT(*) CNT FROM "INFO_SCRAP" S JOIN "INFO_BOARD" B ON (S.INFO_BOARD_NO = B.INFO_BOARD_NO) WHERE S.MEMBER_NO = #{memberNo}
AND B.PROGRM_NM LIKE '%' || #{query} || '%'
)
